mapper.select:
SELECT count(*)
FROM ur
WHERE email_id = #{email_id}

SELECT count(*)
FROM ur
WHERE ur_nk = #{userNick}

SELECT ur.ur_pw
FROM ur
WHERE ur_id = #{ur_id};

mapper.update:
UPDATE ur
SET ur_nk  = #{ur_nk},
    loc_si = #{loc_si},
    loc_gu = #{loc_gu},
    ur_img = #{ur_img}
WHERE ur_id = #{ur_id};

UPDATE ur
SET ur_pw = #{new_pass}
WHERE ur_id = #{login_id};
